import React from 'react';
import { useState, useEffect } from 'react';
import {  ethers } from 'ethers';
import './App.css';

function App() {
  const votingAddress = "0xDB0CB3A2f0969cF59a9B5B603E7470D7985a6b9c";
  const adminAdress="0x419573d6955B1DF992Fe0d4687a5D4349B80C096";

  const [error, setError] = useState('');
  const [account,setAccount]=useState([]);
  const [data, setData] = useState({});

  useEffect(() => { 
      getAccounts();
      fetchData()
    }, []);

    async function getAccounts() {
      if(typeof window.ethereum !== 'undefined') {
          let accounts = await window.ethereum.request({method: 'eth_requestAccounts'});
          setAccount(accounts);
        }
    }

    async function fetchData() {
      if(typeof window.ethereum !== 'undefined') {
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const contract = new ethers.Contract(votingAddress, Voting.abi, provider);
        try {
          var  balance =0;
          balance  = await contract.getContractBalance();
          console.log("balance", parseFloat(String(balance)))
          const object = {"balance": balance}
          setData(object);
        }
        catch(err) {
          setError(err.message);
          console.log("Ereur fetch : ", err.message);
        }
      }
    }

  return (
    <div className="App">
      {error && <p>{error}</p>}
    </div>
  );
}

export default App;
