import React from 'react';
import {  ethers } from 'ethers';
import { useState, useEffect } from 'react';

import '../App.css';


function AdminMenu(props) {
    const [error, setError] = useState('');
    const [voters, setVoters] = useState('');

    useEffect(() => { 
        fetchVoters();
      }, []);

      async function fetchVoters() {
          try {
            let voters  = await props.contract.state();
            setVoters(voters)
            console.log("voters", voters)
          }
          catch(err) {
            setError(err.message);
            console.log("Ereur fetch : ", err.message);
          }
      }
    
    return (
        <div className="AdminMenu">
        
        {error && <p>{error}</p>}
        {voters && <p>{voters}</p>}
        {props.state.state===0 && <button className="AdminButton">Open proposals registration</button>}
        {props.state.state===1 && <button className="AdminButton">Close proposals registration</button>}
        {props.state.state===2 && <button className="AdminButton">Open votes</button>}
        {props.state.state===3 && <button className="AdminButton">Close votes</button>}
        {/* {props.state.state==4 && <button className="AdminButton">Reset ?</button>} */}
        </div>
    );
}






export default AdminMenu;
