import React from 'react';
import { useState, useEffect } from 'react';
import {  ethers } from 'ethers';
import Voting from './artifacts/contracts/Voting.sol/Voting.json';
import {votingAddress} from './utils/contract'
import {Background} from './components/Background';
import Popup from './components/Popup';
import './App.css';


function App() {

  const adminAddress="0x419573d6955B1DF992Fe0d4687a5D4349B80C096";

  const [error, setError] = useState('');
  const [account,setAccount]=useState([]);
  const [data, setData] = useState({});
  const [state, setState] = useState({});
  const [proposal, setProposal] = useState("");
  const [proposalsList, setProposalsList] = useState("");
  const [winningList, setWinningList] = useState("");
  const [isOpen, setIsOpen] = useState(false);

  const togglePopup = () => {
    setIsOpen(!isOpen);
  }

  useEffect(() => { 
      getAccounts();
      fetchData();
    }, []);

    async function getAccounts() {
      if(typeof window.ethereum !== 'undefined') {
          let accounts = await window.ethereum.request({method: 'eth_requestAccounts'});
          setAccount(accounts);
        }
    }

  async function fetchData() {
    if(typeof window.ethereum !== 'undefined') {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const contract = new ethers.Contract(votingAddress, Voting.abi, provider);
      try {
        let state  = await contract.state();
        setState(state);
        if(state===1 ||state===2||state===3){
          let proposals = [...await contract.getProposals()];
          setProposalsList(proposals);
        }else if(state===5){
          let winners = [...await contract.getWinningList()];
          setWinningList(winners);
        }
/*         return(

            <div class="modalDialog">
              <div>
                <a href="#close" title="Close" class="close" onclick="this.style.visibility='hidden';">X</a>
                <h2>Modal Box </h2>
                <p>This modal box is created using the powers of CSS3.</p>
              </div>
            </div>
        ) */

      }
      catch(err) {
        setError(err.message);
        console.log("Ereur fetch : ", err.message);
      }
    }
  }

  async function register() {
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    const contract = new ethers.Contract(votingAddress, Voting.abi, signer);
    try {
      const transaction  = await contract.registerVoter();
      await transaction.wait();
      fetchData();
    }
    catch(err) {
      setError(err.message);
      console.log("Ereur fetch : ", err.message);
    }
/*     contract.on("VoterRegistered", ()=> {
      console.log("A new zombie was born1!");
    }).on("error", console.error); */

  }

  async function openProposals() {
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    const contract = new ethers.Contract(votingAddress, Voting.abi, signer);
    try {
      const transaction  = await contract.openProposals();
      await transaction.wait();
      fetchData();
    }
    catch(err) {
      setError(err.message);
      console.log("Ereur fetch : ", err.message);
    }
  }

  async function sendProposal(_proposal) {
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    const contract = new ethers.Contract(votingAddress, Voting.abi, signer);
    try {
      const transaction  = await contract.sendProposal(_proposal);
      await transaction.wait();
      fetchData();
    }
    catch(err) {
      setError(err.message);
      console.log("Ereur fetch : ", err.message);
    }
  }

  async function closeProposals() {
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    const contract = new ethers.Contract(votingAddress, Voting.abi, signer);
    try {
      const transaction  = await contract.closeProposals();
      await transaction.wait();
      fetchData();
    }
    catch(err) {
      setError(err.message);
      console.log("Ereur fetch : ", err.message);
    }
  }

  async function openVotes() {
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    const contract = new ethers.Contract(votingAddress, Voting.abi, signer);
    try {
      const transaction  = await contract.openVotes();
      await transaction.wait();
      fetchData();
    }
    catch(err) {
      setError(err.message);
      console.log("Ereur fetch : ", err.message);
    }
  }

  async function voteProposal(_id) {
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    const contract = new ethers.Contract(votingAddress, Voting.abi, signer);
    try {
      const transaction  = await contract.voteProposal(_id);
      await transaction.wait();
      fetchData();
    }
    catch(err) {
      setError(err.message);
      console.log("Ereur fetch : ", err.message);
    }
  }


  async function closeVotes() {
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    const contract = new ethers.Contract(votingAddress, Voting.abi, signer);
    try {
      const transaction  = await contract.closeVotes();
      await transaction.wait();
      fetchData();
    }
    catch(err) {
      setError(err.message);
      console.log("Ereur fetch : ", err.message);
    }
  }

  async function getWinner() {
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    const contract = new ethers.Contract(votingAddress, Voting.abi, signer);
    try {
      const transaction  = await contract.getWinner();
      await transaction.wait();
      fetchData();
    }
    catch(err) {
      setError(err.message);
      console.log("Ereur fetch : ", err.message);
    }
  }

  const testModal=(a)=>{
    console.log("a:",a);
    return(
      <div className="modalDialog">
          {/* <a href="#close" title="Close" className="close" onclick="this.style.visibility='hidden';">X</a> */}
          <h2>Modal Box {a}</h2>
          <p>This modal box is created using the powers of CSS3.</p>
      </div>
    )
  }

  return (
    <div className="App">
      <Background/>
      <button onClick={()=>togglePopup("success")}>testModal</button>
      {isOpen && 
        <Popup
          content={<>
            <b>Design your Popup</b>
            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor</p>
          </>}
          type={"success"}
          handleClose={togglePopup}
        />
      }
      {account[0] === adminAddress.toLowerCase() && 
        <div className="AdminMenu">
          <p>Hi Admin !</p>
          {state===0 && <button className="AdminButton" onClick={openProposals} >Open proposals registration</button>}
          {state===1 && <button className="AdminButton" onClick={closeProposals}>Close proposals registration</button>}
          {state===2 && <button className="AdminButton" onClick={openVotes}>Open votes</button>}
          {state===3 && <button className="AdminButton" onClick={closeVotes}>Close votes</button>}
        </div> 
      }
      {account[0]&&
        <p className="Account">
          {account[0]}
        </p>
        }
      <div className="Container" >
        {state===0 &&
          <div className="Register">
            <p className="RegisterTitle1">Hello my friend !</p>
            <p className="RegisterTitle2">If you want to participate to the folowing process, you need to register yourself as voter :</p>
            <button className="RegisterButton" onClick={register}> Register now !</button>
          </div>

        }
        {state===1 &&
          <div>
            <form>
              <label>Enter your proposal :
                <input type="text" value={proposal} onChange={(e) => setProposal(e.target.value)}
              />
              </label>
            </form>
            <button onClick={()=>sendProposal(proposal)}> Send proposal</button>
            {proposalsList==="" && <p> Be the first to send your proposal !</p>}
          </div>
        }

        {proposalsList && state!==4 && state!==5 &&
          <div className ="CardContainer">
            {proposalsList.map( (propositions,index)=>(
              <div className ="Card" key={index}>
                <p className ="CardText">{propositions.description}</p>
                {state===3 &&<button onClick={()=>voteProposal(index)}>Vote !</button>}
              </div>
            ))}
          </div>
        }

        {state===4 &&
          <div>
            <button className="AdminButton" onClick={getWinner} >Find the winning proposal(s)</button>
          </div>
        }
        {state===5 && winningList &&
          <div className ="CardContainer">
            {winningList.map( (propositions1,index)=>(
              <div className ="Card" key={index}>
                {/* <p className ="CardText">{propositions1.description}</p> */}
              </div>
            ))}
          </div>
        } 
      </div>
    </div>
  );
}






export default App;
