import React from 'react';
import { useState, useEffect } from 'react';
import {  ethers } from 'ethers';
import Voting from './artifacts/contracts/Voting.sol/Voting.json';

import {Background} from './components/Background';
import './App.css';


function App() {
  const votingAddress = "0x7c8F0673A02C31701619dc9cCe8E53e3415FC523";
  const adminAdress="0x419573d6955B1DF992Fe0d4687a5D4349B80C096";

  const [error, setError] = useState('');
  const [account,setAccount]=useState([]);
  const [data, setData] = useState({});
  const [state, setState] = useState({});

  useEffect(() => { 
      getAccounts();
      fetchState();
    }, []);

    async function getAccounts() {
      if(typeof window.ethereum !== 'undefined') {
          let accounts = await window.ethereum.request({method: 'eth_requestAccounts'});
          setAccount(accounts);
        }
    }

    async function fetchData() {
      if(typeof window.ethereum !== 'undefined') {
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const contract = new ethers.Contract(votingAddress, Voting.abi, provider);
        try {
          var  balance =0;
          balance  = await contract.getContractBalance();
          console.log("balance", parseFloat(String(balance)))
          const object = {"balance": balance}
          setData(object);
        }
        catch(err) {
          setError(err.message);
          console.log("Ereur fetch : ", err.message);
        }
      }
    }

    async function fetchState() {
      if(typeof window.ethereum !== 'undefined') {
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const contract = new ethers.Contract(votingAddress, Voting.abi, provider);
        try {
          let state  = await contract.state();
          console.log("state", state)
          const object = {"state": state}
          setState(object);
        }
        catch(err) {
          setError(err.message);
          console.log("Ereur fetch : ", err.message);
        }
      }
    }

  return (
    <div className="App">
      <Background/>
      {error && <p>{error}</p>}

    </div>
  );
}

export default App;
