import React from 'react';
import { useState, useEffect } from 'react';
import {  ethers } from 'ethers';
import Voting from './artifacts/contracts/Voting.sol/Voting.json';
import {votingAddress} from './utils/contract'
import {Background} from './components/Background';
import AdminMenu from './components/AdminMenu';
import './App.css';


function App() {

  const adminAddress="0x419573d6955B1DF992Fe0d4687a5D4349B80C096".toLowerCase();

  const [error, setError] = useState('');
  const [account,setAccount]=useState([]);
  const [data, setData] = useState({});
  const [state, setState] = useState({});
  const [contract, setContract] = useState({});

  useEffect(() => { 
      getAccounts();
      fetchState();
    }, []);

    async function getAccounts() {
      if(typeof window.ethereum !== 'undefined') {
          let accounts = await window.ethereum.request({method: 'eth_requestAccounts'});
          setAccount(accounts);
        }
    }

  async function fetchState() {
    if(typeof window.ethereum !== 'undefined') {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const contract = new ethers.Contract(votingAddress, Voting.abi, provider);

      setContract(contract);
      try {
        let state  = await contract.state();
        const object = {"state": state}
        setState(object);
      }
      catch(err) {
        setError(err.message);
        console.log("Ereur fetch : ", err.message);
      }
    }
  }

  async function register() {
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    const contract = new ethers.Contract(WCFaddress, WorldCupFever.abi, signer);
    try {
      const transaction  = await contract.registerVoter();
      await transaction.wait();
    }
    catch(err) {
      setError(err.message);
      console.log("Ereur fetch : ", err.message);
    }
}

  return (
    <div className="App">
      <Background/>
      {error && <p>{error}</p>}
      {account[0] === adminAddress &&<AdminMenu state={state} contract={contract}/> }
      <button onClick={register}> Register</button>

    </div>
  );
}






export default App;
